#include <bits/stdc++.h>using namespace std;///// MAKE SURE YOU DO NOT MAKE THE SAME NAME FUNCTION IN 2 DIFFERENT HEADER FILE IN THE SAME PROJECTvoid printSymbolTable(char Operator[],string identifier[],char punctuation[]){	cout << endl<< endl<< endl;	cout << "Identifiers -> ";	for (int i = 0; i < ID_Flag; i++)	{		cout << identifier[i] << " ";	}	cout << endl;	cout << "Operators -> ";	for (int i = 0; i < OP_Flag; i++)	{		cout << Operator[i] << " ";	}	cout << endl;	cout << "Punctuations -> ";	for (int i = 0; i < Pn_Flag; i++)	{		cout << punctuation[i] << " ";	}	cout << endl;}